import React, { useState } from 'react';
import './NavBar.scss';
import { ReactComponent as Search } from '../../../img/icons/search.svg';
import { ReactComponent as Menu } from '../../../img/icons/menu.svg';
import Input from '../Input/Input';

const NavBar = () => {
    const [search, setSearch] = useState(false);

    const handelSearch = () => {
        setSearch(!search);
    };

    const handelKey = (e) => {
        e.key === 'Enter' && setSearch(!search);
    };

    return (
        <nav className='navbar'>
            <div className='container'>
                <div className='navbar__wrap'>
                    <div className='navbar__logo'>blog.mercuryo</div>
                    <div className='navbar__menu'>
                        <ul
                            className={
                                !search
                                    ? 'navbar__list'
                                    : 'navbar__list navbar__list_hide'
                            }
                        >
                            <li>
                                <a href='/'>Articles</a>
                            </li>
                            <li>
                                <a href='/'>Insights</a>
                            </li>
                            <li>
                                <a href='/'>Success stories</a>
                            </li>
                            <li>
                                <a href='/'>Announcements</a>
                            </li>
                            <li>
                                <a href='/'>Round-up</a>
                            </li>
                        </ul>

                        {!search && <Search onClick={handelSearch} />}

                        <div
                            className={
                                !search
                                    ? 'navbar__search'
                                    : 'navbar__search navbar__search_show'
                            }
                        >
                            <Input label='search' handlerKey={handelKey} />
                        </div>
                    </div>
                </div>
            </div>
        </nav>
    );
};

export default NavBar;
