import React from 'react';
import './Input.scss';
import { ReactComponent as Blot } from '../../../img/icons/blot.svg';

const Input = ({ label }) => {
    return (
        <div className='input'>
            <input type='text' placeholder={label} />
            <Blot />
        </div>
    );
};

export default Input;
